import React, { useState, useEffect } from "react";
import "./pages.css";
import { TabList, Tab, Widget, Blockie, Tooltip, Tag, Icon, Table, Form } from "web3uikit";
import { Link } from "react-router-dom";
import { useLocation } from "react-router";
import { useMoralis } from "react-moralis" ;

const Proposal = () => {

    const { state: proposalDetails } = useLocation();
    const { Moralis, isInitialized } = useMoralis();
    const [LatestVote, setLatestVote] = useState();
    const [percUp, setPercUp] = useState(0);
    const [percDown, setPercDown] = useState(0);
    const [votes, setVotes] = useState ([]);

    useEffect(() => {
      if (isInitialized) {

        async function getVotes() {

          const Votes = Moralis.Object.extend("Votes");
          const query = new Moralis.Query(Votes);
          query.equalTo("proposal", proposalDetails.id);
          query.descending("createdAt");
          const results = await query.find();
          if (results.length > 0) {
            setLatestVote(results[0].attributes);
            setPercDown(
              (
                (Number(results[0].attributes.votesDown) /
                (Number(results))
                )
              )
            )
          }

        }
        getVotes();
      }
    }, [isInitialized]);


  return (
    <>
    <div className="contentProposal">
        <div className="proposal">
          <Link to="/">
            <div className="backHome">
              <Icon fill="#ffffff" size={20} svg="chevronLeft" />
              Overview
              </div>
          </Link>
        <div>Should we accept Elon Musks $44 Billion offer for our DAO</div>
         <div className="proposalOverview">
         <Tag color={"red"} text={"Rejected"} />
          <div className="proposer">
           <span>Proposed By</span>
          <Tooltip content={""}>
            <Blockie seed={""}/>
           </Tooltip>
           </div>
          </div>
          </div>
           <div className="widgets">
        <Widget info={30} title="Votes For">
          <div className="extraWidgetInfo">
          <div className="extraTitle">{75}%</div>
          <div className="Progress">
          <div
          className="progressPercentage"
          style={{ width: `${75}%`}}
          ></div>
          </div>
          </div>
      </Widget>
        <Widget info={10} title="Votes Against">
          <div className="extraWidgetInfo">
          <div className="extraTitle">{25}%</div>
          <div className="progress">
          <div className="progressPercentage"
          style={{ width: `${25}%` }}
          ></div>
          </div>
          </div>
      </Widget>
        </div>
        <div className="votesDiv">
          <Table
            style={{ width: "60%" }}
            columnsConfig="90% 10%"
            data={votes}
            header={[<span>Address</span>, <span>Vote</span>]}
            pageSize={5}
          />

          <Form
            style={{
              width: "35%",
              height: "250px",
              border: "1px solid rgba(6, 158, 252, 0.2)",            
            }}
            buttonConfig={{
              isLoadingText:"Casting Vote",
              text: "Vote",
              theme: "secondary",
            }}
            data={[
              {
                inputWidth: "100%",
                name: "Cast Vote",
                options: ["For", "Against"],
                type: "radios",
                validation: {
                  required: true,
                },              
              },
            ]}
            onSubmit={(e) => {
              alert("Vote cast");
            }}
            title="Cast Vote"
            />
        </div>

     </div>
     <div className="voting"></div>
    </>
  );
};

export default Proposal;
